import java.util.*;
public class Ac_11022 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int t = in.nextInt();
		int a,b;
		for(int i=1; i<=t; i++)
		{
			a = in.nextInt();
			b = in.nextInt();
			
			System.out.println("Case #"+i+": "+a+" + "+b+" = "+(a+b));
		}
		
	}

}
